Ability.Lua:
if UnitCreatureType(&quot;target&quot;) == &quot;Undead&quot; 
or UnitCreatureType(&quot;target&quot;) == &quot;Demon&quot; 
or UnitRace(&quot;target&quot;) == &quot;Undead&quot; 
or UnitRace(&quot;target&quot;) == &quot;Draenei&quot;
and ( rangeCheck(35395, &quot;target&quot;) or rangeCheck(53595, &quot;target&quot;) )
and CanAttack()
and not CooldownRemains(48817)
then return true end

local fd={UnitBuff(&quot;player&quot;, &quot;Formidable&quot;)}

if UnitBuffID(&quot;player&quot;, 67371)
then return false end

if CanAttack()
and fd[4] ~= 5
and rangeCheck(35395, &quot;target&quot;)
and not CooldownRemains(35395)
then return true end
